import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-createperson',
  templateUrl: './createperson.component.html',
  styleUrls: ['./createperson.component.css']
})
export class CreatepersonComponent {


  constructor(private http : HttpClient) {}

  // per : any = {}


  sendData(name : string, age : number, city: string) {
    this.http.post("http://localhost:8000/persons/", {name: name, age: age, city: city}).subscribe(status => {
      alert(status)
    })
  }

}
<div
  style="
    margin: 10px;
    display: flex;
    justify-content: center;
    flex-direction: column;
  "
>
  Name: <input #name style="padding: 10px" /> <br />
  Age: <input #age style="padding: 10px" /> <br />
  City: <input #city style="padding: 10px" /> <br />
</div>

<button style="pad" (click)="sendData(name.value, +age.value, city.value)">Add</button>
